import { signIn, getSession, useSession } from "next-auth/react";
import Image from "next/image";
import { getLayoutDefault } from "@/layouts/LayoutDefault";
import styles from "@/styles/Login.module.scss";
import { PageHead } from "@/components/PageHead/PageHead";

export default function Login() {
  return (
    <div
      className={styles["Login"]}
      onClick={() =>
        signIn("google", {
          callbackUrl: window.origin + "/",
        })
      }
    >
      <PageHead
        title={"Game Archive - Log in"}
        description={
          "Game archive log in page. Log in to website and enjoy your personalized experience."
        }
        keywords={"game archive, login, user"}
      />
      <button className={styles["Login__Button"]}>
        <Image
          src="data:image/svg+xml;base64,"
          alt="Google"
          height={25}
          width={25}
        />
        <span className={styles["Login__Button__Span"]}>
          Log in with Google
        </span>
      </button>
    </div>
  );
}

Login.getLayout = getLayoutDefault;

export async function getServerSideProps(context) {
  const session = await getSession(context);

  if (session) {
    return {
      redirect: {
        destination: "/",
        permanent: false,
      },
    };
  }

  return {
    props: { session },
  };
}
